import Subject from './modules/subject';

import 'unsemantic/assets/stylesheets/unsemantic-grid-base.css';
import 'unsemantic/assets/stylesheets/unsemantic-grid-desktop.css';
import './index.styl';

const subject = new Subject();

export default subject;

const $ = require('imports-loader?$=jQuery!jquery/dist/jquery');
const remark = require('exports-loader?remark!remark-slide/out/remark');
const URI = require('urijs/src/URI');

import githugSvg from '../assets/github.svg';
import homeSvg from '../assets/home.svg';
import { extend } from './utils';

/**
 * A subject in a course that can be displayed as a Remark HTML slideshow.
 *
 * Various methods of this class can be used to customize the runtime behavior
 * and look-and-feel of the slideshow before calling {@link Subject#start}.
 *
 * The `src/subject/index.js` file in your Courses MD repository should provide
 * a Subject instance that you can customize.
 */
export default class Subject {

  /**
   * Constructs a new subject with the default configuration.
   *
   * The subject will have 1 or 2 default links:
   *
   * * If a `subjectUrl` is configured, a link to the subject's Markdown source
   *   will be added.
   * * A link to the home page will be added.
   */
  constructor() {

    /**
     * @private
     */
    this.config = JSON.parse($('meta[name="config"]').attr('content'));

    /**
     * @private
     */
    this.links = [];

    /**
     * @private
     */
    this.logoOptions = undefined;

    if (this.config.subjectUrl) {
      this.addLink({
        url: this.config.subjectUrl,
        imageUrl: githugSvg,
        class: 'source-link',
        width: 24,
        height: 24,
        alt: 'Source code',
        title: 'Source code'
      });
    }

    this.addLink({
      url: this.homeUrl,
      imageUrl: homeSvg,
      class: 'home-link',
      width: 24,
      height: 24,
      alt: 'Home',
      title: 'Home'
    });
  }

  /**
   * Adds a link to the slideshow (displayed in the top right corner with the
   * default style).
   *
   * Links are inserted into an element with the `slide-links` class which you
   * can customize through CSS.
   *
   * @param {Object} options - Link options.
   * @param {string} options.url - The URL to link to.
   * @param {string} options.imageUrl - The URL of the image to use to display
   * the link.
   * @param {number} [options.width] - The width of the link's image.
   * @param {number} [options.height] - The height of the link's image.
   * @param {string} [options.class] - Class to add to the link element.
   * @param {string} [options.alt] - Alternate text of the link's image.
   * @param {string} [options.title] - Title of the link's image.
   */
  addLink(options) {
    this.links.push(options);
  }

  /**
   * Removes all configured links.
   */
  clearLinks() {
    this.links.length = 0;
  }

  /**
   * Displays a logo with the specified options (in the top-left corner with the default style).
   *
   * The logo will be an element with the `slide-logo` class which you can
   * customize through CSS.
   *
   * @param {Object} options - Logo options.
   * @param {string} options.url - The URL to go to when the logo is
   * clicked.
   * @param {string} options.imageUrl - The URL of the logo's image.
   * @param {number} [options.width] - The width of the logo's image.
   * @param {number} [options.height] - The height of the logo's image.
   */
  setLogo(options) {
    this.logoOptions = options;
  }

  /**
   * The URL to which the default home link points to (customizable at runtime
   * through the `home` URL query param).
   *
   * By default, this is the same value as the `basePath` configuration property
   * provided by the build process. However, it can be customized by providing
   * the `home` URL query param:
   *
   * * If the `home` query param is a GitHub identifier in the `User/Repo`
   * format, the home URL will point to that repository on `https://github.com`.
   * * If the `home` query param is an URL, it will be used as the home URL.
   *
   * @type {string}
   */
  get homeUrl() {

    const currentUri = URI(window.location.href);
    const home = currentUri.search(true).home;

    if (typeof(home) == 'string' && home.match(/^[a-z0-9\-\_\.]+\/[a-z0-9\-\_\.]+(?:#.*)?$/i)) {
      return `https://github.com/${home}`;
    } else if (typeof(home) == 'string') {
      return home;
    } else {
      return this.config.basePath;
    }
  }

  /**
   * A jQuery object that contains an element for each Remark slide in the slideshow.
   *
   * @type {jQuery}
   */
  get slides() {
    if (!this._slides) {
      const slides = $('.remark-slide-content');
      if (slides.length) {
        this._slides = slides;
      }
    }

    return this._slides;
  }

  /**
   * Starts the Remark slideshow.
   *
   * @param {Object} options - Slideshow options.
   * @param {Object} [options.remark] - Configuration options to pass to
   * Remark (see https://github.com/gnab/remark/wiki/Configuration); note that
   * these will be applied to **all** slideshows
   */
  start(options) {
    options = options || {};

    const remarkOptions = extend({}, this.config.remark, options.remark);

    /**
     * The Remark slideshow (only available after calling `start`).
     *
     * @type {Remark}
     */
    this.slideshow = remark.create();

    this.insertLinks();
    this.insertLogo();

    // Make all external links open a new window
    $('a[href]').not('.home-link').not('[href^="#"]').attr('target', '_blank');
  }

  /**
   * Inserts the configured links into the slideshow.
   *
   * @private
   */
  insertLinks() {
    if (!this.links.length) {
      return;
    }

    const $links = $('<div class="slide-links" />');

    this.links.reverse().forEach(link => {

      const $link = $('<a />').attr('href', link.url).prependTo($links);

      if (link.class) {
        $link.addClass(link.class);
      }

      const $linkImg = $('<img />').attr('src', link.imageUrl).appendTo($link);

      if (link.alt) {
        $linkImg.attr('alt', link.alt);
      }

      if (link.title) {
        $linkImg.attr('title', link.title);
      }

      if (link.width) {
        $linkImg.attr('width', link.width);
      }

      if (link.height) {
        $linkImg.attr('height', link.height);
      }
    });

    $links.prependTo(this.slides);
  }

  /**
   * Inserts the configured logo into the slideshow.
   *
   * @private
   */
  insertLogo() {
    if (!this.logoOptions) {
      return;
    }

    const $logo = $('<div class="slide-logo" />');
    const $logoLink = $('<a />').appendTo($logo).attr('href', this.logoOptions.url);
    const $logoImg = $('<img />').appendTo($logoLink).attr('src', this.config.basePath + '/' + this.logoOptions.imageUrl);

    if (this.logoOptions.width) {
      $logoImg.attr('width', this.logoOptions.width);
    }

    if (this.logoOptions.height) {
      $logoImg.attr('height', this.logoOptions.height);
    }

    $logo.prependTo(this.slides);
  }
}

export function extend(target, ...sources) {

  sources.forEach(source => {
    if (!source) {
      return;
    }

    for (var property in source) {
      target[property] = source[property];
    }
  });

  return target;
}

const _ = require('lodash');
const chalk = require('chalk');
const debug = require('debug')('courses-md:build');
const del = require('del');
const EventEmitter = require('events');
const fs = require('fs-extra');
const globby = require('globby');
const marked = require('marked');
const path = require('path');
const prettyBytes = require('pretty-bytes');

const { compileSlides } = require('../slides');
const { getTemplate } = require('../templates');
const { runWebpack } = require('../utils');
const webpackBuildConfig = require('./webpack.build');

module.exports = async function build(config, eventEmitter = new EventEmitter()) {

  if (fs.existsSync(config.absoluteBuildDir)) {
    await del(config.absoluteBuildDir);
  }

  await fs.mkdirs(config.absoluteBuildDir);

  const [ homeBundleResult, subjectBundleResult ] = await Promise.all([
    await buildHomeBundle(config).then(result => {
      eventEmitter.emit('build:home-bundle', result);
      return result;
    }),
    await buildSubjectBundle(config).then(result => {
      eventEmitter.emit('build:subject-bundle', result);
      return result;
    })
  ]);

  const homeBundlePath = homeBundleResult.bundlePath;
  const subjectBundlePath = subjectBundleResult.bundlePath;

  await Promise.all([
    buildHome(config, homeBundlePath).then(result => eventEmitter.emit('build:home', result)),
    buildSubjects(config, subjectBundlePath).then(result => eventEmitter.emit('build:subjects', result))
  ]);

  eventEmitter.emit('build');
};

async function buildHomeBundle(config) {

  const entryPointPath = config.absoluteHomeEntryPointFile;
  if (!(await fs.pathExists(entryPointPath))) {
    return {
      entryPointPath: entryPointPath,
      saved: false
    };
  }

  const buildDir = config.absoluteBuildDir;
  const bundleName = 'home';

  const stats = await runWebpack(webpackBuildConfig(entryPointPath, buildDir, bundleName));

  stats.assets.forEach(asset => {
    debug(`${chalk.green(path.join(config.absoluteBuildDir, asset.name))} ${chalk.magenta(prettyBytes(asset.size))}`)
  });

  const bundlePath = path.join(buildDir, stats.assetsByChunkName.main);

  return {
    entryPointPath: entryPointPath,
    bundlePath: bundlePath,
    saved: true
  };
}

async function buildSubjectBundle(config) {

  const entryPointPath = config.absoluteSubjectEntryPointFile;
  const buildDir = config.absoluteBuildDir;
  const bundleName = 'subject';

  const stats = await runWebpack(webpackBuildConfig(entryPointPath, buildDir, bundleName));

  stats.assets.forEach(asset => {
    debug(`${chalk.green(path.join(config.absoluteBuildDir, asset.name))} ${chalk.magenta(prettyBytes(asset.size))}`)
  });

  const bundlePath = path.join(buildDir, stats.assetsByChunkName.main);

  return {
    entryPointPath: entryPointPath,
    bundlePath: bundlePath
  };
}

async function buildHome(config, homeBundlePath) {

  const indexPath = path.join(config.absoluteRootDir, 'README.md');
  const indexContents = await fs.readFile(indexPath, { encoding: 'utf8' });

  const templateFunc = await getTemplate('index.html');
  const compiledIndexContents = templateFunc({
    title: config.title,
    contents: marked(indexContents),
    homeBundlePath: homeBundlePath ? `./${path.relative(config.absoluteBuildDir, homeBundlePath)}` : undefined
  });

  const compiledIndexPath = path.join(config.absoluteBuildDir, 'index.html');
  await fs.writeFile(compiledIndexPath, compiledIndexContents, { encoding: 'utf-8' });

  return {
    indexPath: indexPath,
    compiledIndexPath: compiledIndexPath
  };
}

async function buildSubjects(config, subjectBundlePath) {

  const patterns = [ '**/*.*' ];
  patterns.push(...config.ignore.map(ignore => `!${ignore}`));

  const files = await globby(patterns, { cwd: config.absoluteSubjectsDir });

  await Promise.all(files.map(async file => {

    if (file.match(/\.md$/)) {
      return compileSlides(config, file, subjectBundlePath);
    }

    const sourcePath = path.join(config.absoluteSubjectsDir, file);
    const targetPath = path.join(config.absoluteBuildDir, 'subjects', file);

    await fs.copy(sourcePath, targetPath);

    debug(`${chalk.yellow(sourcePath)} -> ${chalk.green(targetPath)}`);
  }));
}

const path = require('path');

module.exports = function(entryPointPath, bundleDir, bundleName) {
  return {
    entry: entryPointPath,
    module: {
      rules: [
        {
          test: /\.css$/,
          use: [
            require.resolve('style-loader'),
            require.resolve('css-loader')
          ]
        },
        {
          test: /\.(gif|jpg|png|svg|eot|ttf|woff|woff2)$/,
          use: [
            {
              loader: require.resolve('file-loader'),
              options: {
                emitFile: true,
                name: `[name]-[hash].[ext]`
              }
            }
          ]
        }
      ]
    },
    output: {
      filename: `${bundleName}-[hash].js`,
      path: bundleDir
    },
    target: 'web'
  };
}

const commander = require('commander');

const Config = require('./config');
const pkg = require('../package');
const build = require('./commands/build');
const publish = require('./commands/publish');
const serve = require('./commands/serve');
const watch = require('./commands/watch');

module.exports = program;

async function program(argv = process.argv) {

  let action = defaultAction;

  commander
    .option('-c, --config <file>', 'Path to the configuration file (defaults to "./config.js")')
    .option('-b, --build <dir>', 'Path to the build directory where HTML slideshows are saved (defaults to "./build")')
    .option('-t, --title <title>', 'Home page title (defaults to "Courses MD")')
    .option('--base-url <url>', 'Base URL at which the published slides will be available')
    .option('--repo-url <url>', 'Web URL of the repository where the Markdown source is committed (this should be the URL to the repository on GitHub, for example, not the Git URL)')
    .option('--publish-url <url>', 'Git URL of the repository to publish the HTML slides to')
    .option('--publish-branch <branch>', 'Branch to publish the HTML slides in (e.g. "gh-pages")')
    .option('--publish-version <version>', 'Version sub-directory to publish the slides in (defaults to the current year)')
    .version(pkg.version);

  commander
    .command('build')
    .description('Build the home page and all subjects\' HTML slideshows')
    .action(() => {
      action = build;
    });

  commander
    .command('publish')
    .description('Build and commit the home page and all subjects\' HTML slideshows into a Git repository (e.g. for GitHub Pages)')
    .action(() => {
      action = publish;
    });

  commander
    .command('serve')
    .description('Serve the static files in the build directory')
    .action(() => {
      action = serve;
    });

  commander
    .command('watch')
    .description('Watch for changes and recompile the home page and subjects when files are modified')
    .action(() => {
      action = watch;
    });

  commander.parse(argv);

  const config = await new Config().load(commander);

  return Promise
    .resolve(action(config))
    .catch(err => console.warn(err));
}

async function defaultAction(config) {
  await build(config);
  serve(config);
  watch(config);
}

const EventEmitter = require('events');

const build = require('../build');
const { logCompiledFile, logProgress } = require('../logger');

module.exports = async function buildCommand(config) {

  const eventEmitter = new EventEmitter();

  eventEmitter.on('build:home-bundle', result => {
    if (result.saved) {
      logCompiledFile(config, result.entryPointPath, result.bundlePath);
    }
  });

  eventEmitter.on('build:subject-bundle', result => logCompiledFile(config, result.entryPointPath, result.bundlePath));
  eventEmitter.on('build:home', result => logCompiledFile(config, result.indexPath, result.compiledIndexPath));
  eventEmitter.on('build:subjects', () => logCompiledFile(config, `${config.absoluteSubjectsDir}/**/*`, config.absoluteBuildDir));

  const building = build(config, eventEmitter);

  await logProgress(building, 'Building...', 'Build successful');
};

const publish = require('../publish');

module.exports = async function publishCommand(config) {
  await publish(config);
};

const getPort = require('get-port');
const liveServer = require('live-server');

module.exports = serve;

async function serve(config) {

  const port = config.port || await getPort({ port: 3000 });

  liveServer.start({
    port: port,
    root: config.absoluteBuildDir,
    open: true,
    browser: process.env.BROWSER
  });
}

const chalk = require('chalk');
const chokidar = require('chokidar');
const debug = require('debug')('watch');
const fs = require('fs-extra');
const path = require('path');

const { compileSlides } = require('../slides');

module.exports = watch;

function watch(config) {

  const watchers = [];

  debug(`Watching ${config.absoluteSubjectsDir}...`);
  const watcher = chokidar.watch('.', {
    cwd: config.absoluteSubjectsDir,
    ignored: [ '**/.*', '**/node_modules/**/*' ],
    ignoreInitial: true
  });

  // TODO: watch src

  watchers.push(watcher);

  watcher
    .on('add', fileChangeHandler(config))
    .on('change', fileChangeHandler(config));

  return {
    close: watchers => watcher.close()
  };
}

function fileChangeHandler(config) {
  return async function(relativeFilePath) {

    const filePath = path.join(config.absoluteSubjectsDir, relativeFilePath);
    debug(`${chalk.yellow(filePath)} changed...`);

    if (filePath.match(/\.md$/)) {
      const subjectBundlePath = await findSubjectBundle(config);
      compileSlides(config, path.relative(config.absoluteSubjectsDir, filePath), subjectBundlePath);
    } else {

      const targetPath = path.join(config.absoluteBuildDir, relativeFilePath);

      await fs.copy(filePath, targetPath);

      debug(`${chalk.yellow(filePath)} -> ${chalk.green(targetPath)}`);
    }
  };
}

async function findSubjectBundle(config) {

  const buildFiles = await fs.readdir(config.absoluteBuildDir);
  const matchingFiles = buildFiles.filter(filename => filename.match(/^subject(?:-[a-z0-9]+)?.js$/));

  if (matchingFiles.length === 0) {
    throw new Error(`No subject bundle found in ${config.absoluteBuildDir}; make sure to use "build" at least once before "watch"`);
  } else if (matchingFiles.length != 1) {
    throw new Error(`Multiple subject bundles found in ${config.absoluteBuildDir}: ${matchingFiles.join(', ')}`);
  }

  return path.relative(config.absoluteBuildDir, matchingFiles[0]);
}

const _ = require('lodash');
const fs = require('fs-extra');
const path = require('path');

const { execute } = require('./utils');

const OPTIONS = [
  'config',
  'title',
  'scmVersion',
  'repoUrl',
  'baseUrl',
  'root',
  'subjects',
  'ignore',
  'build',
  'homeEntryPoint',
  'subjectEntryPoint',
  'remark',
  'publish.url',
  'publish.branch',
  'publish.version'
];

/**
 * Configuration object for Courses MD.
 *
 * Configuration options can be provided:
 *
 * * At the command line (e.g. `courses-md --config my-config.js build`)
 * * As environment variables (e.g. `COURSES_MD_CONFIG=my-config.js courses-md build`)
 * * As properties exported from a JavaScript configuration file (`./config.js` by default)
 *
 * Options from the command line take precedence over environment variables, which themselves take precedence over configuration file properties.
 * Not all options can be provided with all 3 techniques, depending on the type.
 *
 * The following options are available:
 *
 * CLI                           | Environment                   | Config file       | Purpose
 * :---                          | :---                          | :---              | :---
 * `-c, --config <file>`         | `$COURSES_MD_CONFIG`          |                   | Path to the configuration file (defaults to `./config.js`)
 * `-b, --build <dir>`           | `$COURSES_MD_BUILD`           | `build`           | Path to the build directory where HTML slideshows are saved (defaults to `./build`)
 * `-t, --title <title>`         | `$COURSES_MD_TITLE`           | `title`           | Home page title (defaults to "Courses MD")
 *                               |                               | `remark`          | Configuration options to pass to Remark (see https://github.com/gnab/remark/wiki/Configuration); note that these will be applied to **all** slideshows
 * `--base-url <url>`            | `$COURSES_MD_BASE_URL`        | `baseUrl`         | Base URL at which the published slides will be available
 * `--repo-url <url>`            | `$COURSES_MD_REPO_URL`        | `repoUrl`         | Web URL of the repository where the Markdown source is committed (this should be the URL to the repository on GitHub, for example, not the Git URL)
 * `--publish-url <url>`         | `$COURSES_MD_PUBLISH_URL`     | `publish.url`     | Git URL of the repository to publish the HTML slides to
 * `--publish-branch <branch>`   | `$COURSES_MD_PUBLISH_BRANCH`  | `publish.branch`  | Branch to publish the HTML slides in (e.g. "gh-pages")
 * `--publish-version <version>` | `$COURSES_MD_PUBLISH_VERSION` | `publish.version` | Version sub-directory to publish the slides in
 */
class Config {
  constructor() {
    this.setDefaultOptions();
  }

  get absoluteBuildDir() {
    return path.resolve(this.absoluteRootDir, this.build);
  }

  get absoluteHomeEntryPointFile() {
    return path.resolve(this.absoluteRootDir, this.homeEntryPoint);
  }

  get absoluteSubjectEntryPointFile() {
    return path.resolve(this.absoluteRootDir, this.subjectEntryPoint);
  }

  get absoluteRootDir() {
    return path.resolve(this.root);
  }

  get absoluteSubjectsDir() {
    return path.resolve(this.absoluteRootDir, this.subjects);
  }

  getEnvVar(name) {
    return process.env[`COURSES_MD_${name}`];
  }

  async getSourceUrl() {
    if (!this.repoUrl) {
      return;
    }

    const scmVersion = await this.loadScmVersion();
    return scmVersion ? `${this.repoUrl}/tree/${scmVersion}` : undefined;
  }

  async load(options) {
    options = options || {};

    this.setDefaultOptions();

    const rootDir = path.resolve(options.root || process.env.ROOT || this.root);
    const configFile = path.resolve(rootDir, options.config || process.env.CONFIG || this.config);
    const configFileRequired = options.config || process.env.CONFIG || false;

    await this.loadFileOptions(configFile, configFileRequired);

    this.setEnvironmentOptions();
    this.set(options);

    return this;
  }

  async loadFileOptions(file, required = true) {

    const exists = await fs.pathExists(file);
    if (!exists) {
      if (required) {
        throw new Error(`Configuration path "${file}" does not exist`);
      } else {
        return this;
      }
    }

    const options = await Promise.resolve(require(file));
    this.set(options);

    if (options.publish && options.publish.afterBuild) {
      this.publish.afterBuild = options.publish.afterBuild;
    }

    return this;
  }

  async loadScmVersion() {
    if (typeof(this.scmVersion) == 'function') {
      return Promise.resolve(this.scmVersion(this));
    } else {
      return this.scmVersion;
    }
  }

  async loadDefaultScmVersion() {
    return execute([ 'git', 'rev-parse', '--verify', 'HEAD' ], { cwd: this.absoluteRootDir }).then(result => result.toString().trim()).catch(() => undefined);
  }

  setEnvironmentOptions() {
    return this.set({
      title: this.getEnvVar('TITLE'),
      scmVersion: this.getEnvVar('SCM_VERSION'),
      repoUrl: this.getEnvVar('REPO'),
      baseUrl: this.getEnvVar('BASE_URL'),
      root: this.getEnvVar('ROOT'),
      subjects: this.getEnvVar('SUBJECTS'),
      build: this.getEnvVar('BUILD'),
      homeEntryPoint: this.getEnvVar('HOME_ENTRY_POINT'),
      subjectEntryPoint: this.getEnvVar('SUBJECT_ENTRY_POINT'),
      publish: {
        url: this.getEnvVar('PUBLISH_URL'),
        branch: this.getEnvVar('PUBLISH_BRANCH'),
        version: this.getEnvVar('PUBLISH_VERSION')
      }
    });
  }

  setDefaultOptions() {

    this.remark = {};
    this.publish = {};

    return this.set({
      config: 'config.js',

      title: 'Courses MD',
      scmVersion: () => this.loadDefaultScmVersion(),

      repoUrl: undefined,
      baseUrl: undefined,

      root: '.',

      subjects: 'subjects',
      ignore: [ '**/node_modules/**' ],

      build: 'build',
      homeEntryPoint: 'src/home/index.js',
      subjectEntryPoint: 'src/subject/index.js',

      publish: {
        branch: 'master',
        version: new Date().getFullYear().toString()
      }
    });
  }

  set(options) {

    OPTIONS.forEach(property => {
      const value = _.get(options, property);
      if (value !== undefined) {
        _.set(this, property, value);
      }
    });

    return this;
  }

  validate() {

    this.validateString('title');
    this.validateString('repoUrl');
    this.validateString('baseUrl');
    this.validateString('root');
    this.validateString('subjects');
    this.validateString('build');
    this.validateString('homeEntryPoint');
    this.validateString('subjectEntryPoint');

    if (!_.isArray(this.ignore)) {
      throw new Error(`"ignore" property must be an array, got ${typeof(this.ignore)}: ${this.ignore}`);
    }

    if (!_.isObject(this.remark)) {
      throw new Error(`"remark" property must be an object, got ${typeof(this.remark)}: ${this.remark}`);
    }

    if (!_.includes([ 'string', 'function' ], typeof(this.scmVersion))) {
      throw new Error(`"scmVersion" property must be a string or a function, got ${typeof(this.scmVersion)}: ${this.remark}`);
    }

    if (this.publish.url !== undefined) {
      this.validateString('publish.url');
    }

    if (this.publish.version !== undefined) {
      this.validateString('publish.version');
    }

    return this;
  }

  validateString(property) {
    const value = _.get(this, property);
    if (typeof(value) != 'string') {
      throw new Error(`"${property}" property must be a string, got ${typeof(value)}: ${value}`);
    }
  }
}

